def extrai_circuito(tbl, linha_de_alvo, qtd_vars, estados, metodo='sintese', ancillas=0):
    logger.info(f'Aplicando {metodo} com {ancillas} ancillas e {qtd_vars} variaveis, com alvo na linha {linha_de_alvo}')
    logger.debug(f'Estados = {estados}')

    n = len(tbl[0])
    logger.debug(f'N = {n}')
    # logger.debug(f'tbl = {tbl}')

    tbl_vdd_alvo = gera_tabela_verdade(n)
    # logger.debug(f'tbl_vdd = {list(gera_tabela_verdade(n))}')

    f_alvo = extrai_coluna_da_matriz(tbl_vdd_alvo, linha_de_alvo)
    logger.debug(f'F_alvo = {f_alvo}')

    f = extrai_coluna_da_matriz(tbl, linha_de_alvo)
    logger.debug(f'F = {f}')

    fxor = gera_f1_xor(f, f_alvo)
    logger.debug(f'FXOR = {fxor}')

    # expressao = processa_permutacao(f1, n - 1, estados, prefixo='b', valor_inicial=linha_de_alvo)
    expressao = processa_permutacao(fxor, n - 1, estados, prefixo='b', alvo=linha_de_alvo)
    logger.info(f'Expressão encontrada: {expressao=}')

    expressao_tratada = substitui_simbolos(expressao)
    expressao_tratada = expressao_tratada.replace('(', '').replace(')', '')
    # logger.debug(f'Expressão final: {expressao_tratada=}')

    return gera_circuito(expressao_tratada, linha_de_alvo, ancillas, qtd_vars)


def otimiza_usando_metodo_generico(circ, metodo_usado, ancillas):
    qtd_vars = circ.tamanho_circuito
    # logger.debug(f'Qtd. Variaveis ==> {qtd_vars}')

    linhas_alvo_utilizadas = list(circ.obtem_linhas_de_alvo_usadas())
    # logger.debug(f'Linhas de alvo: {linhas_alvo_utilizadas}')

    logger.debug(f'Qtd. Vars = {qtd_vars}')
    estados = gera_estados(qtd_vars, linhas_alvo_utilizadas, prefixo='b')
    logger.debug(f'Estados = {estados}')
    # estados = gera_estados(qtd_vars, prefixo='b')
    # logger.debug(f'Estados ==> {estados}')

    tbl = circ.obtem_tabela_verdade(ancillas=ancillas)
    # logger.debug(f'Tabela Verdade Original ==> {tbl}')

    circuito_otimizado = Circuito()
    for linha_de_alvo in linhas_alvo_utilizadas:
        circuito = extrai_circuito(tbl, linha_de_alvo, qtd_vars, estados, metodo_usado, ancillas)
        logger.debug(f'Circuito extraído:\n{circuito}')
        circuito_otimizado = circuito_otimizado + circuito
        logger.debug(f'Circuito otimizado extraído:\n{circuito_otimizado}')

    # verifica_otimizacao(circ, circuito_otimizado)

    return circuito_otimizado


def otimiza_subcircuito_usando_pos_sintese(circ, ancillas=0):
    metodo_usado = 'pos-sintese'

    circuito_otimizado = otimiza_usando_metodo_generico(circ, metodo_usado, ancillas)
    return circuito_otimizado


def executa_pos_sintese(circ: Circuito):
    if circ.gc == 0:
        return circ

    ancillas = circ.bits_extras

    subcircuitos = gera_subcircuitos_usando_alvos3(circ, qtd_alvos=1)
    logger.info(f'Qtd. Subcircuitos = {len(subcircuitos)}')
    logger.info(f'Qtd. Média de portas = {len(circ) / len(subcircuitos)}')

    subcircuitos_otimizados = list()
    for i, subcircuito in enumerate(subcircuitos):
        # logger.debug(f'Subcircuito Original #{i}\n{subcircuito}')

        # chama a função de otimização de pós-sintese
        subcircuito_otimizado = otimiza_subcircuito_usando_pos_sintese(subcircuito, ancillas)
        subcircuitos_otimizados.append(subcircuito_otimizado)

        # logger.debug(f'Subcircuito Otimizado #{i}\n{subcircuito_otimizado}')

    # logger.debug(f'Subcircuitos:\n{subcircuitos}')
    circuito_otimizado = junta_subcircuitos(subcircuitos_otimizados)
    # logger.info(f'Subcircuitos juntos:\n{circuito_otimizado}')
    return circuito_otimizado


def otimiza_circuito_com_reed(nome_tfc, metodo='pos-sintese'):
    logger.info(f'Nome TFC = {nome_tfc}')

    circ = realiza_leitura_tfc(nome_tfc)
    # logger.info(f'Circuito Original:\n{circ}')

    if metodo == 'pos-sintese':
        circuito_otimizado = executa_pos_sintese(circ)
        return circuito_otimizado

    elif metodo == 'sintese':
        raise Exception(f'Método de síntese não implementado')

    else:
        raise Exception(f'Método {metodo} desconhecido')


